const axios = require('axios');


//Change the URL As per the api key
const url = 'https://reqres.in/api/users';

//Change the value for Adding the new uses
const data = { id:8, email: "Kishan@gmail.com", first_name:"Kishan", last_name:"Naik" };

//The USer will as per the id you can the id value as u need
const deleteuser = { id:7};

//This id is used update User 
const UpdateUserID = { id:1};




async function getUsers(){
    try {
            const response = await axios.get(url);
            console.log(response.data);
    } catch (error) {
        console.error("404",error);
    }
}

async function addUser(){
    try {
            const response = await axios.post(url,data);
            console.log(response.data);
    } catch (error) {
            console.error(error);
    }
}

async function removeUser(){
    try {
            const response = await axios.delete(url,deleteuser);
            console.log("Delect the User with ID ",deleteuser);
    } catch (error) {
            console.error(error);
        
    }
}

